import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { Parking } from 'src/app/models/parking';
import { FileService } from 'src/app/services/file.service';
import { ParkService } from 'src/app/services/parkService/park.service';

@Component({
  selector: 'app-ajouter-parking',
  templateUrl: './ajouter-parking.component.html',
  styleUrls: ['./ajouter-parking.component.css']
})
export class AjouterParkingComponent implements OnInit {
  parkingForm!: FormGroup;
  imagePreview: string | ArrayBuffer | null = null;
  uploadProgress: number | null = null;
  fileToUpload: File | null = null; 

  constructor(
    private formBuilder: FormBuilder,
    private parkingService: ParkService,
    private router: Router,
    private fileService: FileService
  ) {}

  ngOnInit(): void {
    this.parkingForm = this.formBuilder.group({
      nom: ['', Validators.required],
      adresse: ['', Validators.required],
      description: ['', Validators.required],
      capaciteTotale: ['', [Validators.required, Validators.min(0)]],
      latitude: ['', [Validators.required, Validators.min(-90), Validators.max(90)]],
      longitude: ['', [Validators.required, Validators.min(-180), Validators.max(180)]]
    });
  }

  onFileSelected(event: Event): void {
    const element = event.currentTarget as HTMLInputElement;
    let fileList: FileList | null = element.files;
    if (fileList) {
      this.fileToUpload = fileList[0];
      const reader = new FileReader();
      reader.onload = () => this.imagePreview = reader.result;
      reader.readAsDataURL(this.fileToUpload);
    }
  }

  onSubmit() {
    if (this.parkingForm.valid && this.fileToUpload) {
      this.uploadFileAndSubmitForm();
    } else {
      console.error('Form is invalid or file not selected');
    }
  }

  private uploadFileAndSubmitForm() {
    this.fileService.uploadFile(this.fileToUpload!).subscribe(
      uploadResponse => {
        this.addParking();
      },
      uploadError => {
        console.error('Error uploading file:', uploadError);
      }
    );
  }

  private addParking() {
    console.log(this.parkingForm.value)
    const newParking: Parking = {
      ...this.parkingForm.value,
      image: this.fileToUpload!.name
    };
    this.parkingService.addParking(newParking).subscribe(
      response => {
        this.router.navigate(['/admin/listParking']);
      },
      error => {
        console.error('Error adding Parking:', error);
      }
    );
  }
}
<div class="row myrow">
    <form [formGroup]="parkingForm" (ngSubmit)="onSubmit()">
        <div class="form-group">
            <label for="nom">Nom:</label>
            <input type="text" id="nom" name="nom" formControlName="nom" class="form-control">
            <div class="alert alert-danger" *ngIf="parkingForm.get('nom')?.touched && parkingForm.get('nom')?.errors?.['required']">
                Nom is required!
            </div>
        </div>
        <div class="form-group">
            <label for="adresse">Adresse:</label>
            <input type="text" id="adresse" name="adresse" formControlName="adresse" class="form-control">
            <div class="alert alert-danger" *ngIf="parkingForm.get('adresse')?.touched && parkingForm.get('adresse')?.errors?.['required']">
                Adresse is required!
            </div>
        </div>
        <div class="form-group">
            <label for="capaciteTotale">Capacité Totale:</label>
            <input type="number" id="capaciteTotale" name="capaciteTotale" formControlName="capaciteTotale" class="form-control">
            <div class="alert alert-danger" *ngIf="parkingForm.get('capaciteTotale')?.touched && parkingForm.get('capaciteTotale')?.errors?.['required']">
                Capacité Totale is required!
            </div>
        </div>
        <div class="form-group">
            <label for="description">Description:</label>
            <textarea id="description" name="description" formControlName="description" class="form-control"></textarea>
            <div class="alert alert-danger" *ngIf="parkingForm.get('description')?.touched && parkingForm.get('description')?.errors?.['required']">
                Description is required!
            </div>
        </div>
        <div class="form-group">
            <label for="latitude">Latitude:</label>
            <input type="number" id="latitude" name="latitude" formControlName="latitude" class="form-control">
            <div class="alert alert-danger" *ngIf="parkingForm.get('latitude')?.touched && parkingForm.get('latitude')?.errors?.['required']">
                Latitude is required!
            </div>
        </div>
        <div class="form-group">
            <label for="longitude">Longitude:</label>
            <input type="number" id="longitude" name="longitude" formControlName="longitude" class="form-control">
            <div class="alert alert-danger" *ngIf="parkingForm.get('longitude')?.touched && parkingForm.get('longitude')?.errors?.['required']">
                Longitude is required!
            </div>
        </div>
        <div class="form-group">
            <label for="image">Image</label>
            <input type="file" id="image" (change)="onFileSelected($event)" class="form-control">
            <div *ngIf="uploadProgress">
                <div>Uploading... {{ uploadProgress }}%</div>
                <progress [value]="uploadProgress" max="100"></progress>
            </div>
            <img *ngIf="imagePreview" [src]="imagePreview" class="thumbnail-preview">
        </div>

        <div class="form-group">
            <button type="submit" class="btn btn-primary mybutton" [disabled]="parkingForm.invalid">Save</button>
        </div>
    </form>
</div>
